int main ( ) { int i , j , n , c , t , ans ; int A [ 100000 ] ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { ans = 0 ; scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & A [ j ] ) ; } j = 0 ; c = 1 ; while ( j < n ) { if ( A [ j ] < A [ j + 1 ] ) { c ++ ; } else { ans = ans + ( c * ( c - 1 ) / 2 ) ; c = 1 ; } j ++ ; } printf ( "%d\n" , ans + n ) ; } return 0 ; }