int a [ 100 ] ; int b [ 100 ] ; int main ( ) { int n , r , i , j , max ; scanf ( "%d" , & n ) ; while ( n -- ) { for ( i = 0 ; i < 100 ; i ++ ) { a [ i ] = 0 ; b [ i ] = 0 ; } scanf ( "%d" , & r ) ; for ( i = 1 ; i <= r ; i ++ ) { if ( i % 2 ) { for ( j = 0 ; j < i ; j ++ ) { scanf ( "%d" , & b [ j ] ) ; if ( j ) b [ j ] += ( ( a [ j ] > a [ j - 1 ] ) ? a [ j ] : a [ j - 1 ] ) ; else b [ j ] += a [ j ] ; } } else for ( j = 0 ; j < i ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; if ( j ) a [ j ] += ( ( b [ j ] > b [ j - 1 ] ) ? b [ j ] : b [ j - 1 ] ) ; else a [ j ] += b [ j ] ; } } if ( ! ( r % 2 ) ) { max = a [ 0 ] ; while ( ( r -- ) - 1 ) { if ( a [ r - 1 ] > max ) max = a [ r - 1 ] ; } } else { max = b [ 0 ] ; while ( ( r -- ) - 1 ) { if ( b [ r - 1 ] > max ) max = b [ r - 1 ] ; } } printf ( "%d\n" , max ) ; } return 0 ; }