int gcd ( int x , int y ) { if ( x == 0 ) return y ; else return gcd ( y % x , x ) ; } int lcm ( int x , int y ) { if ( x % y == 0 && x >= y ) return x ; else if ( x % y == 0 && x < y ) return y ; else return x * y ; } int main ( ) { int i , n , a , b ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; int g = gcd ( a , b ) ; int l = lcm ( a , b ) ; printf ( "%d %d\n" , g , l ) ; } }