int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b ; int ans ; scanf ( "%d %d" , & a , & b ) ; if ( a != 0 && b != 0 ) { if ( a > b ) { ans = a % b ; if ( ans == 0 ) { printf ( "%d %d\n" , b , a ) ; } else printf ( "1 %d\n" , a * b ) ; } else if ( a == b ) { printf ( "%d %d\n" , a , a ) ; } else { ans = b % a ; if ( ans == 0 ) printf ( "%d %d\n" , a , b ) ; else printf ( "1 %d\n" , a * b ) ; } } else { printf ( "0 0\n" ) ; } } return 0 ; }