int main ( ) { int t , i , min , a , b , x , y , j , m ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d %d" , & x , & y ) ; a = x , b = y ; min = a < b ? a : b ; int gcd = 1 ; if ( min == 1 ) gcd = 1 ; else { long int pi , pj ; int pk = 0 , psize ; long int p [ min ] ; for ( pi = 2 ; pi <= min ; pi ++ ) { int prime = 1 ; for ( pj = 2 ; pj <= pi / 2 ; pj ++ ) { if ( pi % pj == 0 ) { prime = 0 ; break ; } } if ( prime == 1 ) p [ pk ++ ] = pi ; psize = pk ; } int k = 0 ; while ( a > p [ k ] && b > p [ k ] ) { if ( a % p [ k ] != 0 || b % p [ k ] != 0 ) { k ++ ; if ( k == psize ) break ; } else { gcd *= p [ k ] ; a /= p [ k ] ; b /= p [ k ] ; } } } long long int lcm ; a = x , b = y ; lcm = gcd * ( a / gcd ) * ( b / gcd ) ; printf ( "%d %lld\n" , gcd , lcm ) ; } return 0 ; }