int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , i , j , sum = 0 ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] < a [ i + 1 ] && i + 1 < n ) { sum = sum + a [ i ] ; a [ i + 1 ] = sum ; } else if ( a [ i + 1 ] < a [ i ] && i + 1 < n ) { sum = sum + a [ i + 1 ] ; a [ i + 1 ] = sum ; } } printf ( "%d\n" , sum ) ; } return 0 ; }