int a [ 10000 ] ; int main ( ) { int t , i , s , j , max ; scanf ( "%d" , & t ) ; while ( t -- ) { int k = 0 , max = 0 ; scanf ( "%d" , & s ) ; for ( i = 0 ; i < ( ( s * ( s + 1 ) ) / 2 ) ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < s ; i ++ ) { j = 0 ; for ( ; j <= i ; k ++ ) { if ( i > 0 ) { if ( j == 0 || ( j == 1 && i == 1 ) ) { a [ k ] += a [ ( ( ( i - 1 ) * i ) / 2 ) ] ; } else if ( j != i ) { a [ k ] += max ( a [ k - i ] , a [ ( k - i ) - 1 ] ) ; } else a [ k ] += a [ ( k - i ) - 1 ] ; } if ( ( i == s - 1 ) && max < a [ k ] ) { max = a [ k ] ; } j ++ ; } } k = 0 ; printf ( "%d\n" , max ) ; } return 0 ; }