int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] , sum = 0 ; for ( int i = 0 ; i < n ; ++ i ) scanf ( "%d" , & a [ i ] ) ; for ( int i = 0 ; i < n - 1 ; ++ i ) { if ( a [ i ] > a [ i + 1 ] && a [ i ] != 0 ) { a [ i ] = 0 ; sum = sum + a [ i + 1 ] ; } if ( a [ i ] < a [ i + 1 ] && a [ i ] != 0 ) { int temp = a [ i ] ; a [ i ] = a [ i + 1 ] ; a [ i + 1 ] = temp ; a [ i ] = 0 ; sum = sum + a [ i + 1 ] ; } } printf ( "%d\n" , sum ) ; } return 0 ; }