long long int gcd ( long long int a , long long int b ) { if ( a == 0 ) return b ; else return gcd ( a - b , b ) ; } int main ( ) { int t , i ; unsigned long long int a , b , c , d ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%llu" , & a ) ; scanf ( "%llu" , & b ) ; if ( a > b ) c = gcd ( a , b ) ; else c = gcd ( b , a ) ; d = ( a * b ) / c ; printf ( "%llu %llu\n" , c , d ) ; } return 0 ; }