int main ( ) { int t = 0 , c = 0 ; scanf ( "%d" , & t ) ; for ( c = 0 ; c < t ; c ++ ) { int a = 0 , b = 0 , max = 0 , min = 0 , hcf = 1 , lcm = 1 , i = 0 ; scanf ( "%d %d" , & a , & b ) ; max = MAX ( a , b ) ; min = MIN ( a , b ) ; for ( i = 1 ; i > 0 ; i ++ ) { if ( ( ( a % i ) == 0 ) && ( ( b % i ) == 0 ) ) { hcf = i ; } if ( ( i % a ) == 0 && ( i % b ) == 0 ) { lcm = i ; break ; } } printf ( "%d %d\n" , hcf , lcm ) ; } return 0 ; }