int main ( ) { long long t , n , a [ 100000 ] , i , j , sum ; scanf ( "%lld" , & t ) ; while ( t -- ) { scanf ( "%lld" , & n ) ; long long b [ 100000 ] ; sum = 1 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; b [ i ] = 1 ; } for ( i = 0 ; i < n - 1 ; i ++ ) { j = i + 1 ; if ( a [ i ] <= a [ j ] ) b [ j ] = b [ i ] + 1 ; sum += b [ j ] ; } printf ( "%lld" , sum ) ; } return 0 ; }