int main ( void ) { int test ; scanf ( "%d" , & test ) ; while ( test -- ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( int i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; int cnt = 0 ; while ( n > 1 ) { if ( a [ 0 ] < a [ 1 ] ) { cnt += a [ 0 ] ; if ( n > 2 ) a [ 0 ] = a [ 2 ] ; for ( int k = 1 ; k < n ; k ++ ) { a [ k ] = a [ k + 2 ] ; } } else if ( a [ 0 ] >= a [ 1 ] ) { cnt += a [ 1 ] ; for ( int k = 0 ; k < n ; k ++ ) a [ k ] = a [ k + 1 ] ; } n -- ; } printf ( "%d\n" , cnt ) ; } return 0 ; }