int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { unsigned long n , count , min , ans ; scanf ( "%lu" , & n ) ; unsigned long arr [ n ] ; for ( count = 0 ; count < n ; count ++ ) scanf ( "%lu" , & arr [ count ] ) ; min = arr [ 0 ] ; for ( count = 1 ; count < n ; count ++ ) if ( arr [ count ] < min ) min = arr [ count ] ; ans = min * ( n - 1 ) ; printf ( "%lu\n" , ans ) ; } return 0 ; }