int main ( ) { int n , t , * * a , * b , i , j , k , sum , max ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; a = ( int * * ) calloc ( n , sizeof ( int * ) ) ; b = ( int * ) calloc ( n , sizeof ( int ) ) ; for ( j = 0 ; j < n ; j ++ ) { a [ j ] = ( int * ) calloc ( n , sizeof ( int ) ) ; } for ( j = 0 ; j < n ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & a [ j ] [ k ] ) ; } } b [ 0 ] = a [ 0 ] [ 0 ] ; for ( j = 0 ; j < n - 1 ; j ++ ) { for ( k = j ; k >= 0 ; k -- ) { sum = b [ k ] + a [ j + 1 ] [ k + 1 ] ; if ( sum > b [ k + 1 ] ) { b [ k + 1 ] = sum ; } sum = b [ k ] + a [ j + 1 ] [ k ] ; if ( sum > b [ k ] ) { b [ k ] = sum ; } } } max = 0 ; for ( j = 0 ; j < n ; j ++ ) { if ( max < b [ j ] ) { max = b [ j ] ; } } free ( a ) ; free ( b ) ; printf ( "%d\n" , max ) ; } return 0 ; }