int main ( ) { int t ; unsigned int a , b , i , j ; unsigned int ans1 = 0 , ans2 = 0 ; scanf ( "%d" , & t ) ; while ( t > 0 ) { scanf ( "%d%d" , & a , & b ) ; if ( a < b ) { for ( i = a ; i > 0 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { ans1 = i ; break ; } } } else { for ( i = b ; i > 0 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { ans1 = i ; break ; } } } for ( i = 1 ; i <= a * b ; i ++ ) { if ( i % a == 0 && i % b == 0 ) { ans2 = i ; break ; } } printf ( "%d\t%d" , ans1 , ans2 ) ; t -- ; } return 0 ; }