typedef long long int ll ; ll gcd ( ll , ll ) ; int main ( ) { ll a , b , t , i , min , max , h , l ; scanf ( "%lld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; max = a > b ? a : b ; min = a < b ? a : b ; h = gcd ( max , min ) ; l = ( a * b ) / h ; printf ( "%lld %lld\n" , h , l ) ; } } ll gcd ( ll a , ll b ) { if ( a % b == 0 ) return b ; else return gcd ( b , a % b ) ; }