int nextElement ( int i , int l , int n , int * a ) { if ( l != ( n - 1 ) ) { int sum1 = a [ i ] + nextElement ( i + l + 1 , l + 1 , n , a ) ; int sum2 = a [ i ] + nextElement ( i + l + 2 , l + 1 , n , a ) ; return max ( sum1 , sum2 ) ; } else { return a [ i ] ; } } int max ( int sum1 , int sum2 ) { if ( sum1 > sum2 ) return sum1 ; else return sum2 ; } int main ( ) { unsigned int n ; scanf ( "%d" , & n ) ; unsigned int len = ( ( n * ( n + 1 ) ) / 2 ) ; unsigned int a [ len ] , i = 0 , l = 0 , sum [ n ] ; while ( i < len ) { scanf ( "%d" , & a [ i ] ) ; i ++ ; } i = 0 ; l = 0 ; unsigned val = nextElement ( i , l , n , a ) ; printf ( "\n%d" , val ) ; return 0 ; }