int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n = 0 , i = 0 ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; int min = 999999 ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] < min ) min = a [ i ] ; } int cost = 0 ; cost = min * ( n - 1 ) ; printf ( "%d\n" , cost ) ; } }