int main ( ) { int n ; scanf ( "%d" , & n ) ; int i ; int size = 0 ; int array [ 1000 ] ; int j ; int count ; int nd ; int ans [ n ] ; int a = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & size ) ; for ( j = 0 ; j < size ; j ++ ) { scanf ( "%d" , & array [ j ] ) ; } count = 1 ; nd = 0 ; for ( j = 0 ; j < size ; j ++ ) { if ( j == size - 1 ) { if ( array [ size - 1 ] >= array [ size - 2 ] ) { nd += ( count * ( count + 1 ) ) / 2 ; } else nd += 1 ; } else { if ( array [ j + 1 ] >= array [ j ] ) count ++ ; else { nd += ( count * ( count + 1 ) ) / 2 ; count = 1 ; } } } ans [ a ] = nd ; a ++ ; } for ( i = 0 ; i < n ; i ++ ) { printf ( "%d\n" , ans [ i ] ) ; } return 0 ; }