int main ( void ) { int cases , counter ; scanf ( "%d" , & cases ) ; for ( counter = 0 ; counter < cases ; counter ++ ) { int lines , inner , outer , input , totall = 0 , totalr = 0 ; scanf ( "%d" , & lines ) ; for ( outer = 0 ; outer < lines ; outer ++ ) { for ( inner = 0 ; inner <= outer ; inner ++ ) { scanf ( "%d" , & input ) ; if ( inner == 0 ) totall += input ; if ( inner == outer ) totalr += input ; } } printf ( "%d\n" , ( totall > totalr ) ? totall : totalr ) ; } return 0 ; }