int gcd ( int x , int y ) { if ( y > x ) { y = y + x ; x = y - x ; y = y - x ; } if ( ! ( x % y ) ) { return y ; } else { gcd ( y , x % y ) ; } } int main ( ) { int tst , i , j ; scanf ( "%d" , & tst ) ; for ( i = 0 ; i < tst ; i ++ ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; long int hcf = gcd ( a , b ) ; long int lcm = a * ( b / hcf ) ; printf ( "%ld %ld\n" , hcf , lcm ) ; } }