int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n , i , sum = 0 ; scanf ( "%lld" , & n ) ; long long int a [ n ] , b [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; b [ 0 ] = 1 ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] >= a [ i - 1 ] ) b [ i ] = b [ i - 1 ] + 1 ; else b [ i ] = 1 ; } for ( i = 0 ; i < n ; i ++ ) sum = sum + b [ i ] ; printf ( "%lld\n" , sum ) ; } return 0 ; }