int max ( int a , int b ) { if ( a > b ) return a ; else return b ; } int min ( int x , int y ) { if ( x < y ) return x ; else return y ; } int main ( void ) { int i , t , a , b , x , y , gcd , lcm , m , r ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; x = a , y = b ; a = max ( a , b ) ; m = min ( a , b ) ; r = a % b ; while ( a % b ) { a = m , b = r ; if ( a % b == 0 ) gcd = b ; else r = a % b ; m = min ( a , b ) ; } lcm = ( x * y ) / gcd ; printf ( "%d\t%d\n" , gcd , lcm ) ; } return 0 ; }