long gcd ( long a , long b ) { if ( a % b == 0 ) return b ; gcd ( b , a % b ) ; } int main ( void ) { long T , LCM [ 1000 ] , GCD [ 1000 ] , i , a , b ; do { scanf ( "%ld" , & T ) ; } while ( T > 1000 && T < 1 ) ; for ( i = 0 ; i < T ; i ++ ) { do { scanf ( "%ld%ld" , & a , & b ) ; } while ( a < 0 && a > 1000000 && b < 0 && b > 1000000 ) ; GCD [ i ] = gcd ( a , b ) ; LCM [ i ] = ( a * b ) / GCD [ i ] ; } for ( i = 0 ; i < T ; i ++ ) printf ( "%ld %ld\n" , GCD [ i ] , LCM [ i ] ) ; return 0 ; }