int gcd ( long int a , long int b ) { int min , i , max ; if ( a < b ) { min = a ; max = b ; } else { min = b ; max = a ; } for ( i = min ; i > 0 ; i -- ) { if ( min % i == 0 && max % i == 0 ) { return i ; } } } int lcm ( long int a , long int b ) { long int min , i , max , temp ; if ( a < b ) { min = a ; max = b ; } else { min = b ; max = a ; } for ( i = 1 ; i <= 10 ; i ++ ) { temp = max * i ; if ( temp % min == 0 ) { return temp ; } } } int main ( ) { long int a , b , t ; scanf ( "%ld" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; printf ( "%d %d\n" , gcd ( a , b ) , lcm ( a , b ) ) ; } return 0 ; }