int main ( ) { int i , j , t , a , b , gcd ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; if ( a < b ) { for ( j = 1 ; j <= a ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { gcd = j ; } } } else if ( b < a ) { for ( j = 1 ; j <= b ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { gcd = j ; } } } printf ( "%d %d\n" , gcd , ( a * b / gcd ) ) ; } return 0 ; }