long int gcd ( int a , int b ) { if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } long int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t != 0 ) { long int a , b , x , y ; scanf ( "%ld%ld" , & x , & y ) ; printf ( "%ld %ld" , gcd ( x , y ) , lcm ( x , y ) ) ; t -- ; } }