int main ( void ) { long long count , sum = 0 ; int t , n ; register int a , b ; READ_INT ( t ) ; while ( t -- ) { READ_INT ( n ) ; READ_INT ( a ) ; count = 0 ; sum = 0 ; for ( n -- ; n > 0 ; n -- ) { READ_INT ( b ) ; if ( b >= a ) { count ++ ; } else { sum += ( count * ( count + 1 ) >> 1 ) ; count = 1 ; } a = b ; } sum += ( count * ( count + 1 ) >> 1 ) ; WRITE_INT ( sum ) ; putchar ( '\n' ) ; } return 0 ; }