int main ( ) { int t , n , i , j , num , sum1 , sum2 , k ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { sum1 = 0 ; sum2 = 0 ; scanf ( "%d" , & n ) ; if ( n == 0 ) printf ( "%d" , 0 ) ; else if ( n == 1 ) { scanf ( "%d" , & num ) ; printf ( "%d" , num ) ; } else { for ( j = 0 ; j < n ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & num ) ; if ( k == 0 ) sum1 += num ; if ( k == j ) sum2 += num ; } } if ( sum1 > sum2 ) printf ( "%d" , sum1 ) ; else printf ( "%d" , sum2 ) ; } } return 0 ; }