long int lcm ( long int , long int ) ; int main ( void ) { long int t , a , b , g , k = 0 , l ; scanf ( "%ld" , & t ) ; while ( t > k ) { l = 0 ; g = 0 ; scanf ( "%ld%ld" , & a , & b ) ; g = gcd ( a , b ) ; l = ( a * b ) / g ; printf ( "%ld %ld\n" , g , l ) ; k ++ ; } return 0 ; } int gcd ( long int a , long int b ) { long int c ; while ( a != 0 ) { c = a ; a = b % a ; b = c ; } return b ; }