int gcd ( long long int a , long long int b ) ; int main ( ) { long long int t , a , b , hcf , lcm ; scanf ( "%lld\n" , & t ) ; while ( t -- ) { scanf ( "%lld %lld" , & a , & b ) ; hcf = gcd ( a , b ) ; lcm = ( a * b ) / hcf ; printf ( "%lld %lld\n" , hcf , lcm ) ; } return 0 ; } int gcd ( long long int a , long long int b ) { long long int dividend = a >= b ? a : b ; long long int divisor = a <= b ? a : b ; while ( divisor != 0 ) { int remainder = dividend % divisor ; dividend = divisor ; divisor = remainder ; } return dividend ; }