int main ( ) { int test_cases , t ; int * arr , size ; int prev ; int i , sum ; scanf ( "%d" , & test_cases ) ; for ( t = 0 ; t < test_cases ; t ++ ) { sum = 0 ; scanf ( "%d" , & size ) ; arr = ( int * ) calloc ( size , sizeof ( int ) ) ; for ( i = 0 ; i < size ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } prev = arr [ 0 ] ; for ( i = 1 ; i < size ; i ++ ) { if ( arr [ i ] <= prev ) { sum += arr [ i ] ; prev = arr [ i ] ; } else { sum += prev ; } } printf ( "%d\n" , sum ) ; free ( arr ) ; } return 0 ; }