long get_sum ( long sum , int * a , int i , int j , int n ) { long sum1 = 0 , sum2 = 0 ; if ( i >= n ) { return sum ; } sum += a [ i * n + j ] ; sum1 = get_sum ( sum , a , ( i + 1 ) , j , n ) ; sum2 = get_sum ( sum , a , ( i + 1 ) , ( j + 1 ) , n ) ; if ( sum1 > sum2 ) { return sum1 ; } else { return sum2 ; } } int main ( void ) { int test_cases ; int n , i , j ; scanf ( "%d" , & test_cases ) ; while ( test_cases -- ) { scanf ( "%d" , & n ) ; int a [ n ] [ n ] ; long sum = 0 ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } sum = get_sum ( sum , a , 0 , 0 , n ) ; printf ( "%ld\n" , sum ) ; } return 0 ; }