int main ( ) { int t , n , sum , i , j , b , c , t1 , t2 , test ; scanf ( "%d" , & t ) ; while ( t ) { sum = 0 ; scanf ( "%d" , & n ) ; int a [ n ] [ n ] ; for ( j = 0 ; j < n ; j ++ ) { for ( i = 0 ; i <= j ; i ++ ) scanf ( "%d" , & a [ j ] [ i ] ) ; } i = n - 1 ; for ( j = 0 ; j < n ; j ++ ) { test = a [ i ] [ j ] ; c = i ; b = j ; while ( c > 0 ) { if ( b == 0 ) test += a [ c - 1 ] [ b ] ; else { t1 = a [ c - 1 ] [ b ] ; t2 = a [ c - 1 ] [ b - 1 ] ; test += ( t1 > t2 ) ? t1 : t2 ; if ( t2 > t1 ) b -- ; } c -- ; } sum = ( test > sum ) ? test : sum ; } printf ( "%d\n" , sum ) ; t -- ; } return 0 ; }