main ( ) { unsigned long int A , B , num , den , rem , i , t ; scanf ( "%lu" , & t ) ; unsigned long int ans [ t ] [ 2 ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lu%lu" , & A , & B ) ; if ( A > B ) { num = A ; den = B ; } else { num = B ; den = A ; } rem = A % B ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } ans [ i ] [ 0 ] = den ; ans [ i ] [ 1 ] = ( A * B ) / ans [ i ] [ 0 ] ; } for ( i = 0 ; i < t ; i ++ ) printf ( "%lu %lu\n" , ans [ i ] [ 0 ] , ans [ i ] [ 1 ] ) ; }