int main ( ) { long int t , a , b , i ; long int l , g ; scanf ( "%ld" , & t ) ; while ( t -- ) { scanf ( "%ld" , & a ) ; scanf ( "%ld" , & b ) ; if ( a > b ) { for ( i = 1 ; i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { g = i ; } } } else { for ( i = 1 ; i <= a ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { g = i ; } } } l = ( a * b ) / g ; printf ( "%ld %ld\n" , g , l ) ; } }