int main ( void ) { int n , i , k , j ; scanf ( "%d" , & n ) ; for ( k = 0 ; k < n ; k ++ ) { int x , z , sum = 0 ; scanf ( "%d" , & x ) ; int a [ x ] [ x ] ; for ( i = 0 ; i < x ; i ++ ) { for ( j = 0 ; j < x ; j ++ ) { a [ i ] [ j ] = 0 ; } } for ( i = 0 ; i < x ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } z = 0 ; for ( j = 0 ; j <= i ; j ++ ) { if ( a [ i ] [ j ] > z ) { z = a [ i ] [ j ] ; } } sum = sum + z ; } printf ( "%d\n" , sum ) ; } return 0 ; }