int gcdf ( int m , int n ) { if ( m == 0 || n == 0 ) return 0 ; int rem = m % n ; if ( rem == 0 ) return n ; return gcdf ( n , rem ) ; } int main ( void ) { int test ; scanf ( "%d" , & test ) ; while ( test -- ) { int a , b ; scanf ( "%d%d" , & a , & b ) ; int gcd = gcdf ( a , b ) ; long long int lcm = ( long long int ) ( a * b ) / gcd ; printf ( "%d %lld\n" , gcd , lcm ) ; } return 0 ; }