long int gcd ( long int a , long int b ) { if ( b == 0 ) { return a ; } gcd ( b , a % b ) ; } int main ( void ) { long int t ; scanf ( "%ld" , & t ) ; while ( t > 0 ) { long int a , b ; scanf ( "%ld %ld" , & a , & b ) ; long int g = gcd ( a , b ) ; long int l = ( a * b ) / g ; printf ( "%ld %ld\n" , g , l ) ; t -- ; } }