int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; else if ( a == b ) return a ; else if ( a > b ) return gcd ( a - b , b ) ; else if ( b > a ) return gcd ( b - a , a ) ; } int main ( ) { int t , a , b , hcf , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; hcf = gcd ( a , b ) ; lcm = a * b / hcf ; printf ( "%d %d\n" , hcf , lcm ) ; } return 0 ; }