int main ( ) { long long int t , n , i , a [ 50000 ] , min , ans ; scanf ( "%lld" , & t ) ; while ( t -- ) { for ( i = 0 ; i < 50000 ; i ++ ) a [ i ] = 0 ; min = 0 ; ans = 0 ; scanf ( "%lld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; min = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] < min ) min = a [ i ] ; } ans = ( n - 1 ) * min ; printf ( "\n%lld" , ans ) ; } return 0 ; }