int main ( ) { int * * a , i , n , j , k , maxm , t , p ; scanf ( "%d" , & t ) ; for ( p = 0 ; p < t ; p ++ ) { k = 0 ; scanf ( "%d" , & n ) ; a = ( int * * ) malloc ( n * sizeof ( int * ) ) ; for ( i = 0 ; i < n ; i ++ ) { a [ i ] = ( int * ) malloc ( ( i + 1 ) * sizeof ( int ) ) ; for ( j = 0 ; j < i + 1 ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } j = 0 ; for ( i = 0 ; i < n ; i ++ ) { k = k + a [ i ] [ j ] ; } maxm = k ; for ( j = 0 ; j < n ; j ++ ) { for ( i = n - 1 ; i >= 0 && i > j ; i -- ) { k = k + a [ i ] [ j + 1 ] - a [ i ] [ j ] ; if ( k > maxm ) maxm = k ; } } printf ( "%d\n" , maxm ) ; } return 0 ; }