int main ( ) { int t , n , a [ 50 ] , i , j , k , sum ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { sum = 0 ; scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } for ( k = 0 ; k < n - 1 ; k ++ ) { if ( a [ k ] < a [ k + 1 ] ) { a [ k + 1 ] = a [ k ] ; sum = sum + a [ k ] ; } else sum = sum + a [ k + 1 ] ; } printf ( "%d\n" , sum ) ; } return ( 0 ) ; }