int main ( ) { unsigned int T , j , c ; T = j = c = 0 ; scanf ( "%u" , & T ) ; for ( j = 0 ; j < T ; j ++ ) { unsigned long N ; unsigned long A , B , i , op , c ; N = A = B = op = c = i = 0 ; scanf ( "%llu" , & N ) ; scanf ( "%lu %lu" , & A , & B ) ; if ( A >= B ) { c = B ; op += B ; } else { c = A ; op += A ; } for ( i = 2 ; i < N ; i ++ ) { scanf ( "%lu" , & A ) ; if ( c >= A ) { c = A ; op += A ; } else { op += c ; } } printf ( "%lu\n" , op ) ; } return 0 ; }