int main ( ) { int t , i , j ; scanf ( "%d" , & t ) ; int a [ t * 2 ] ; for ( i = 0 ; i < t * 2 ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < t * 2 ; i += 2 ) { for ( j = a [ i ] ; j >= 1 ; j -- ) if ( a [ i ] % j == 0 && a [ i + 1 ] % j == 0 ) { printf ( "%d" , j ) ; break ; } for ( j = 1 ; j <= a [ i ] * a [ i + 1 ] ; j ++ ) if ( j % a [ i ] == 0 && j % a [ i + 1 ] == 0 ) { printf ( " %d" , j ) ; break ; } printf ( "\n" ) ; } return 0 ; }