int main ( ) { int a , b , temp ; int gcd , repeat = 0 , n , lcm , tempa , tempb ; scanf ( "%d" , & n ) ; for ( ; repeat < n ; repeat ++ ) { scanf ( "%d%d" , & a , & b ) ; tempa = a ; tempb = b ; if ( a < b ) { temp = a ; a = b ; b = temp ; } while ( a != 0 && b != 0 ) { temp = a ; a = b ; b = temp % b ; } gcd = a ; printf ( "%d" , a ) ; a = tempa / gcd ; b = tempb / gcd ; lcm = gcd * a * b ; printf ( " %d" , lcm ) ; } return 0 ; }