int main ( ) { int i , j , k , a , op1 , op2 ; scanf ( "%d" , & k ) ; while ( k -- ) { scanf ( "%d" , & a ) ; int p [ a + 1 ] [ a + 1 ] ; for ( i = 1 ; i <= a ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) { scanf ( "%d" , & p [ i ] [ j ] ) ; } } for ( i = a - 1 ; i > 0 ; i -- ) { op1 = 0 ; op2 = 0 ; for ( j = i ; j > 0 ; j -- ) { op1 = p [ i + 1 ] [ j ] + p [ i ] [ j ] ; op2 = p [ i + 1 ] [ j + 1 ] + p [ i ] [ j ] ; if ( op1 >= op2 ) p [ i ] [ j ] = op1 ; else p [ i ] [ j ] = op2 ; } } printf ( "%d\n" , p [ 0 ] [ 0 ] ) ; } return 0 ; }