int main ( ) { int t , a , b , i , j , k ; scanf ( "%d" , & t ) ; int g [ t ] , l [ t ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; for ( j = 1 ; j < a + b ; j ++ ) { if ( a % j && b % j == 0 ) g [ i ] = j ; else g [ i ] = 1 ; } for ( k = a * b ; k > 0 ; k -- ) { if ( k % a == 0 && k % b == 0 ) { l [ i ] = k ; break ; } } } for ( i = 0 ; i < t ; i ++ ) printf ( "%d %d\n" , g [ i ] , l [ i ] ) ; }