int main ( ) { int T ; scanf ( "%d" , & T ) ; long N ; long * A ; long i ; long min = 999999 ; long cost ; int c = 0 ; while ( c < T ) { scanf ( "%ld" , & N ) ; A = ( long * ) malloc ( N * sizeof ( long ) ) ; for ( i = 0 ; i < N ; i ++ ) { scanf ( "%ld" , & A [ i ] ) ; } for ( i = 0 ; i < N ; i ++ ) { if ( min > A [ i ] ) { min = A [ i ] ; } } cost = min * ( N - 1 ) ; printf ( "%ld\n" , cost ) ; c = c + 1 ; } }