int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int i , j = 0 , n , k = 1 , sum = 0 ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; if ( n == 1 ) sum = 1 ; while ( j >= 0 && j + 1 < n ) { while ( ( a [ j ] <= a [ j + 1 ] ) && j + 1 < n ) { k ++ ; j ++ ; } sum = sum + ( ( k * ( k + 1 ) ) / 2 ) ; k = 1 ; j ++ ; } printf ( "%d" , sum ) ; } }